/*
 * @brief Quantirize IMU data and feed to CNN model
 * @param None
 * @return None
 */
void model_feed_data(void)
{
	const double scale = QUANTIFICATION_SCALE;
	uint16_t i = 0;
	for (i = 0; i < IMU_SEQUENCE_LENGTH_MAX; i++)
	{
		nnom_input_data[i * 3] = (int8_t)round((float)gyro[i][Roll] / IMU_GYRO_TRANS_RADIAN_CONSTANT * scale);
		nnom_input_data[i * 3 + 1] = (int8_t)round((float)gyro[i][Pitch] / IMU_GYRO_TRANS_RADIAN_CONSTANT * scale);
		nnom_input_data[i * 3 + 2] = (int8_t)round((float)gyro[i][Yaw] / IMU_GYRO_TRANS_RADIAN_CONSTANT * scale);
	}
}

int8_t model_get_output(void)
{
	volatile uint8_t i = 0;
	volatile int8_t max_output = -128;
	int8_t ret = 0;
	model_feed_data();
	model_run(model);
	for (i = 0; i < 13; i++)
	{

#ifdef SERIAL_DEBUG
		printf("Output[%d] = %.2f %%\n", i, (nnom_output_data[i] / 127.0) * 100);
#endif // SERIAL_DEBUG

		if (nnom_output_data[i] >= max_output)
		{
			max_output = nnom_output_data[i];
			ret = i;
		}
	}
	if (max_output < OUPUT_THRESHOLD || ret == NoMotion)
	{
		ret = Unrecognized;
	}
	// put into vitual fifo
	switch (ret)
	{
	case Unrecognized:
		printf("Unrecognized");
		break;
	case RightAngle:
		printf("RightAngle");
		KEY_FIFO_Put(RightAngle); //+10 to match fifo enum

		break;
	case SharpAngle:
		printf("SharpAngle");
		KEY_FIFO_Put(SharpAngle);
		break;
	case Lightning:
		printf("Lightning");
		KEY_FIFO_Put(Lightning);
		break;
	case Triangle:
		printf("Triangle");
		KEY_FIFO_Put(Triangle);
		break;
	case Letter_h:
		printf("Letter_h");
		KEY_FIFO_Put(Letter_h);
		break;
	case letter_R:
		printf("Letter_R");
		KEY_FIFO_Put(letter_R);
		break;
	case letter_W:
		printf("Letter_W");
		KEY_FIFO_Put(letter_W);
		break;
	case letter_phi:
		printf("Letter_phi");
		KEY_FIFO_Put(letter_phi);
		break;
	case Circle:
		printf("Circle");
		KEY_FIFO_Put(Circle);
		break;
	case UpAndDown:
		printf("UpAndDown");
		KEY_FIFO_Put(UpAndDown);
		break;
	case Horn:
		printf("Horn");
		KEY_FIFO_Put(Horn);
		break;
	case Wave:
		printf("Wave");
		KEY_FIFO_Put(Wave);
		break;
	case NoMotion:
		printf("Unrecognized");
		break;
	}
	printf("\n");

	return ret;
}

